#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\C:\Users\zhengwanlun\Desktop\ROYAL\node_modules\.store\@getgrit+cli@0.1.0-alpha.1721334930\node_modules\@getgrit"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/C:/Users/zhengwanlun/Desktop/ROYAL/node_modules/.store/@getgrit+cli@0.1.0-alpha.1721334930/node_modules/@getgrit"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../.store/@getgrit+cli@0.1.0-alpha.1721334930/node_modules/@getgrit/cli/run-marzano.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../.store/@getgrit+cli@0.1.0-alpha.1721334930/node_modules/@getgrit/cli/run-marzano.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../.store/@getgrit+cli@0.1.0-alpha.1721334930/node_modules/@getgrit/cli/run-marzano.js" $args
  } else {
    & "node$exe"  "$basedir/../.store/@getgrit+cli@0.1.0-alpha.1721334930/node_modules/@getgrit/cli/run-marzano.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
